import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor(props) {
      super(props);

      this.state = {
         data: ''
      }
      this.value = {
         data: 'Nao pode estar vazio'
      }
      this.updateState = this.updateState.bind(this);
   };
   updateState(e) {
      if(e.target.value == ''){
        this.setState({data: "Não pode estar vazio"});
      }else{
        this.setState({data: e.target.value});
      }
   }
  render(){

      return(
         <div>
            <h1>Hello World</h1>
            <label>Nome: </label>
            <input type = "text" value = {this.state.data} 
               onChange = {this.updateState} />
            <h4>{this.value.data}</h4>
         </div>
      );
   }
}

export default App;
