import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor(props) {
      super(props);

      this.state = {
         name: '',
         password: ''
      }
      this.updateState = this.updateState.bind(this);
   };
   updateState(e) {
      this.setState({name: e.target.value});
   }
  render(){
    let header = '';
    if (this.state.name == '') {
      header = <h1>Campo n√£o pode estar vazio</h1>;
    } else {
      header = '';
    }
    return(
       <div>
          <h1>Hello World</h1>
          <form>
            <label>Nome: </label>
            <input type = "text" value = {this.state.name} 
               onChange = {this.updateState} />
            {header}
          </form>
       </div>
    );
  }
}

export default App;
